var odata = require('../../');

var server = odata('mongodb://localhost/odata-test');

// init resources
server.use(require('./resources/book'));
server.use(require('./resources/user'));

// init functions
server.use(require('./functions/license'));
server.use(require('./functions/server-time'));

server.listen(3001, function(){
  console.log('OData services has started, you can visit by http://localhost:3000');
});

