var server = require('../simple/index.js');
var data = require("../../test/support/books.json");
var userdata=require("../../test/support/users.json");

console.log(server._db);
model = server._db.model('book');
usermodel=server._db.model('user');
model.remove({}, function(err, result) {
  data.map(function(item) {
    entity = new model(item);
    entity.save();
  });
});
usermodel.remove({},function(err,result){
  userdata.map(function(item){
    entity=new usermodel(item);
    entity.save();
  });
});
